CREATE DATABASE CHAEA;

USE CHAEA;

CREATE TABLE Aprendizaje(id INT AUTO_INCREMENT, texto TEXT, PRIMARY KEY (id));
CREATE TABLE Grupo(id INT AUTO_INCREMENT, texto TEXT, PRIMARY KEY (id));
CREATE TABLE Alumno(id INT AUTO_INCREMENT, grupo INT, PRIMARY KEY (id), FOREIGN KEY (grupo) REFERENCES Grupo(id));
CREATE TABLE Pregunta(id INT AUTO_INCREMENT, texto TEXT, aprendizaje INT, PRIMARY KEY (id), FOREIGN KEY (aprendizaje) REFERENCES Aprendizaje(id));
CREATE TABLE Cuestionario(id INT AUTO_INCREMENT, alumno INT, fecha DATE, cantActivo INT, cantReflexivo INT, cantTeorico INT, cantPragmatico INT, PRIMARY KEY (id), FOREIGN KEY (alumno) REFERENCES Alumno(id));


