INSERT INTO `Pregunta`(`id`, `texto`, `aprendizaje`) VALUES (11,"11. Estoy a gusto siguiendo un orden en las comidas, en el estudio, haciendo ejercicio regularmente.",3);
INSERT INTO `Pregunta`(`id`, `texto`, `aprendizaje`) VALUES (15,"15. Normalmente encajo bien con personas reflexivas, y me cuesta sintonizar con personas demasiado espontáneas, imprevisibles.",3);
