INSERT INTO `Pregunta`(`id`, `texto`, `aprendizaje`) VALUES (25,"25. Me cuesta ser creativo/a, romper estructuras.",3);
INSERT INTO `Pregunta`(`id`, `texto`, `aprendizaje`) VALUES (29,"29. Me molesta que la gente no se tome en serio las cosas.",3);
