INSERT INTO `Pregunta`(`id`, `texto`, `aprendizaje`) VALUES (45,"45. Detecto frecuentemente la inconsistencia y puntos débiles en las argumentaciones de los demás.",3);
INSERT INTO `Pregunta`(`id`, `texto`, `aprendizaje`) VALUES (50,"50. Estoy convencido/a que debe imponerse la lógica y el razonamiento.",3);
